DROP TABLE IF EXISTS `api` ;

CREATE TABLE `api` (
  `api_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT 'API ID',
  `biz_id` int(11) unsigned DEFAULT NULL COMMENT '所属企业ID',
  `project_id` int(11) unsigned DEFAULT NULL COMMENT '所属项目ID',
  `category_id` int(11) unsigned DEFAULT NULL COMMENT '所属分类ID',
  `name` varchar(20) NOT NULL COMMENT '名称；最多20个字符',
  `code` varchar(10) NOT NULL COMMENT '序号；例如USR0等，最多10个字符',
  `url` varchar(30) NOT NULL COMMENT 'URL；相对于API根路径，最多30个字符',
  `url_full` varchar(255) DEFAULT NULL COMMENT '第三方URL；完整URL，非自有API或URL不在根URL下的API有此项，非空时仅显示此项，不显示url字段',
  `description` varchar(255) DEFAULT NULL COMMENT '说明；最多255个字符',
  `params_request` mediumtext COMMENT '请求参数；最多10000',
  `params_respond` mediumtext COMMENT '返回参数；最多10000',
  `sample_request` mediumtext COMMENT '请求示例；最多10000',
  `sample_respond` mediumtext COMMENT '返回示例；最多10000',
  `time_create` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `time_delete` datetime DEFAULT NULL COMMENT '删除时间',
  `time_edit` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后操作时间',
  `creator_id` int(11) unsigned DEFAULT NULL COMMENT '创建者ID',
  `operator_id` int(11) unsigned DEFAULT NULL COMMENT '最后操作者ID',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态；0草稿1正常',
  PRIMARY KEY (`api_id`)
) ENGINE=InnoDB AUTO_INCREMENT=366 DEFAULT CHARSET=utf8 COMMENT='API信息表';

DROP TABLE IF EXISTS `biz` ;

CREATE TABLE `biz` (
  `biz_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '企业ID',
  `name` varchar(30) NOT NULL COMMENT '名称；最多30个字符',
  `description` varchar(100) DEFAULT NULL COMMENT '说明；最多100个字符',
  `brief_name` varchar(10) DEFAULT NULL COMMENT '简称；最多10个字符',
  `url_logo` varchar(255) DEFAULT NULL,
  `time_create` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `time_delete` datetime DEFAULT NULL COMMENT '删除时间',
  `time_edit` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后操作时间',
  `creator_id` int(11) unsigned DEFAULT NULL COMMENT '创建者ID',
  `operator_id` int(11) unsigned DEFAULT NULL COMMENT '最后操作者ID',
  PRIMARY KEY (`biz_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='企业信息表';

DROP TABLE IF EXISTS `category_api` ;

CREATE TABLE `category_api` (
