DROP TABLE IF EXISTS `flow` ;

CREATE TABLE `flow` (
  `flow_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '流程ID',
  `biz_id` varchar(11) COLLATE utf8_unicode_ci NOT NULL COMMENT '所属企业ID',
