DROP TABLE IF EXISTS `meta` ;

CREATE TABLE `meta` (
  `meta_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '参数ID',
  `project_id` varchar(11) NOT NULL COMMENT '所属项目ID',
  `sdk_ios` varchar(10) DEFAULT NULL COMMENT 'iOS最低版本',
  `sdk_android` varchar(10) DEFAULT NULL COMMENT 'Android最低版本',
  `url_wechat` varchar(255) DEFAULT NULL COMMENT '微信公众号二维码URL',
  `url_web` varchar(255) DEFAULT NULL COMMENT 'WEB URL',
  `url_api` varchar(255) DEFAULT NULL COMMENT 'API URL',
  `url_ios` varchar(255) DEFAULT NULL COMMENT 'iOS下载URL',
  `url_android` varchar(255) DEFAULT NULL COMMENT 'Android下载URL',
  `sandbox_url_web` varchar(255) DEFAULT NULL COMMENT 'WEB沙箱环境URL',
  `sandbox_url_api` varchar(255) DEFAULT NULL COMMENT 'API沙箱环境URL',
  `encode` enum('UTF8','GBK','GB2312','ASCII','BIG5') NOT NULL DEFAULT 'UTF8' COMMENT '编码方式；UTF8,GBK,GB2312,ASCII,BIG5，默认UTF-8',
  `protocol` enum('HTTPS','HTTP') NOT NULL DEFAULT 'HTTPS' COMMENT '传输协议；HTTPS,HTTP，默认HTTPS',
  `request_method` enum('POST','GET','PUT','DELETE') NOT NULL DEFAULT 'POST' COMMENT '请求方式；POST、GET、PUT、DELETE等，默认POST',
  `request_format` enum('form-data','x-www-form-urlencoded','raw','binary','JSON','XML') NOT NULL DEFAULT 'form-data' COMMENT '请求格式',
  `respond_format` enum('JSON','XML') NOT NULL DEFAULT 'JSON' COMMENT '响应返回格式；JSON、XML，默认JSON',
  `sign` blob COMMENT '签名方式说明',
  `params_request` blob COMMENT '公共请求参数',
  `params_respond` blob COMMENT '公共返回参数',
  `time_create` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `time_delete` datetime DEFAULT NULL COMMENT '删除时间',
  `time_edit` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后操作时间',
  `creator_id` varchar(11) DEFAULT NULL COMMENT '创建者ID',
  `operator_id` varchar(11) DEFAULT NULL COMMENT '最后操作者ID',
  PRIMARY KEY (`meta_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='参数信息表';

DROP TABLE IF EXISTS `page` ;

CREATE TABLE `page` (
  `page_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '页面ID',
  `biz_id` varchar(11) DEFAULT NULL COMMENT '所属企业ID',
  `project_id` varchar(11) NOT NULL COMMENT '所属项目ID',
  `category_id` varchar(11) DEFAULT NULL COMMENT '所属分类ID',
  `code` varchar(10) NOT NULL COMMENT '序号；例如ACTP1，最多10个字符',
  `name` varchar(20) NOT NULL COMMENT '名称；最多20个字符',
  `description` varchar(255) DEFAULT NULL COMMENT '说明；最多255个字符',
  `code_class` varchar(20) DEFAULT NULL COMMENT '类名',
  `code_function` varchar(20) DEFAULT NULL COMMENT '方法名',
  `private` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否需要登录；MySQL不支持boolean类型，0不需登录1需登录，默认1',
  `return_allowed` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否可返回；移动端“返回”按钮是否可用',
  `nav_top` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否显示标题栏；是1否0',
  `nav_bottom` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '显示导航栏',
  `elements` blob COMMENT '主要视图元素',
  `url_prototype` varchar(255) DEFAULT NULL COMMENT '原型URL',
  `url_design` varchar(255) DEFAULT NULL COMMENT '设计图URL',
  `url_assets` varchar(255) DEFAULT NULL COMMENT '素材URL',
  `note_designer` varchar(255) DEFAULT NULL COMMENT '设计师备注；最多255个字符',
  `onloads` blob COMMENT '载入事件',
  `returns` tinyblob NOT NULL COMMENT '返回事件；移动端点击“返回“后的流程',
  `events` blob COMMENT '页面事件',
  `api_ids` varchar(255) DEFAULT NULL COMMENT '相关API ID们；多个ID间以一个空格分隔',
  `page_ids` varchar(255) DEFAULT NULL COMMENT '相关页面ID们；多个ID间以一个空格分隔',
  `note_developer` varchar(255) DEFAULT NULL COMMENT '开发者备注；最多255个字符',
  `time_create` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `time_delete` datetime DEFAULT NULL COMMENT '删除时间',
  `time_edit` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后操作时间',
  `creator_id` varchar(11) DEFAULT NULL COMMENT '创建者ID',
  `operator_id` varchar(11) DEFAULT NULL COMMENT '最后操作者ID',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态；0草稿1正常',
  PRIMARY KEY (`page_id`)
) ENGINE=InnoDB AUTO_INCREMENT=310 DEFAULT CHARSET=utf8 COMMENT='页面信息表';

DROP TABLE IF EXISTS `project` ;

CREATE TABLE `project` (
