  `operator_id` varchar(11) DEFAULT NULL COMMENT '最后操作者ID',
  PRIMARY KEY (`task_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='任务信息表';

DROP TABLE IF EXISTS `team` ;

CREATE TABLE `team` (
  `team_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '团队ID',
  `biz_id` int(11) unsigned NOT NULL COMMENT '所属企业ID',
  `name` varchar(10) NOT NULL COMMENT '名称；最多10个字符',
