  `avatar` varchar(255) DEFAULT NULL COMMENT '头像图片URL',
  `mobile` varchar(11) NOT NULL COMMENT '手机号',
  `email` varchar(40) DEFAULT NULL COMMENT '电子邮件地址',
  `wechat_open_id` varchar(28) DEFAULT NULL COMMENT '微信用户的openid',
  `role` enum('管理员','成员','经理','设计师','工程师') NOT NULL DEFAULT '成员' COMMENT '角色；默认“成员”，可选''管理员'',''成员'',''经理'',''设计师'',''工程师''',
  `level` tinyint(2) unsigned NOT NULL DEFAULT '10' COMMENT '级别；默认10，最高99',
  `last_login_ip` varchar(39) DEFAULT NULL COMMENT '最后登录IP地址；支持IPv6',
  `last_login_time` int(10) unsigned DEFAULT NULL COMMENT '最后登录时间；UNIX时间戳',
  `time_create` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间；即注册时间',
  `time_delete` datetime DEFAULT NULL COMMENT '删除时间',
  `time_edit` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后编辑时间',
  `creator_id` int(11) unsigned DEFAULT NULL COMMENT '创建者ID',
  `operator_id` int(11) unsigned DEFAULT NULL COMMENT '最后操作者ID',
  PRIMARY KEY (`user_id`),
  KEY `mobile` (`mobile`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='用户信息表';

